mapper.update:
UPDATE MYPAGE
SET MY_IMAGE = #{my_image},
   MY_NICKNAME = #{my_nickname},
   MY_INTRO = #{my_intro, jdbcType=VARCHAR}
WHERE MY_ID = #{my_id}

mapper.select:
SELECT COUNT(*)
FROM HEARTBOARD
WHERE HE_ID = #{my_id}

mapper.update:
UPDATE MARKETUSER
SET US_NICKNAME = #{my_nickname}
WHERE US_ID = #{my_id}

UPDATE MARKETBOARD
SET BO_NICKNAME = #{my_nickname}
WHERE BO_ID = #{my_id}

UPDATE BUYBOARD
SET BUY_NICKNAME = #{my_nickname}
WHERE BUY_ID = #{my_id}

UPDATE HEARTBOARD
SET HE_NICKNAME = #{my_nickname}
WHERE HE_ID = #{my_id}

UPDATE CENTER
SET CE_NICKNAME = #{my_nickname}
WHERE CE_ID = #{my_id}

UPDATE NOTICE
SET NO_NICKNAME = #{my_nickname}
WHERE NO_ID = #{my_id}

UPDATE SELLBOARD
SET SEL_NICKNAME = #{my_nickname}
WHERE SEL_ID = #{my_id}

UPDATE CHATROOM
SET CR_SELLER = #{my_nickname}
WHERE CR_SELLER_ID = #{my_id}

UPDATE CHATROOM
SET CR_BUYER = #{my_nickname}
WHERE CR_BUYER_ID = #{my_id}

UPDATE MYPAGE
SET WD_REASON_PRE = #{wd_reason_pre},
   WD_REMARK_PRE = #{wd_remark_pre, jdbcType=VARCHAR}
WHERE MY_ID = #{my_id}

UPDATE REPLY
SET RE_PROFILE = #{my_image}
WHERE RE_ID = #{my_id}

UPDATE REPLY
SET RE_NAME = #{my_nickname}
WHERE RE_ID = #{my_id}

mapper.select:
SELECT COUNT(my_nickname)
FROM MYPAGE
WHERE MY_NICKNAME = #{my_nickname}

SELECT COUNT(*)
FROM MARKETBOARD
WHERE BO_ID = #{my_id}

SELECT COUNT(*)
FROM BUYBOARD
WHERE BUY_ID = #{my_id}

SELECT COUNT(*)
FROM CHATROOM
WHERE CR_SELLER IN (
   SELECT MY_NICKNAME
   FROM MYPAGE
   WHERE MY_ID = #{my_id}
) OR CR_BUYER IN (
   SELECT MY_NICKNAME
   FROM MYPAGE
   WHERE MY_ID = #{my_id}
)

mapper.update:
UPDATE marketboard
SET bo_PROFILE = #{my_image}
WHERE bo_ID = #{my_id}
