mapper.select:

select re_num, bo_num, re_reply, re_name, re_profile, re_regdate, re_updateDate, re_id
from (
   select /*+ index(reply idx_reply)*/
   rownum rn, re_num, bo_num, re_reply, re_name, re_profile, re_regdate, re_updateDate, re_id
   from (select rownum rn, re_num, bo_num, re_reply, re_name, re_profile, re_regdate, re_updateDate, re_id 
   from reply order by re_num desc)
   where bo_num = #{bo_num}
   and re_num > 0 
   and rownum <= #{cri.pageNum}*#{cri.amount}
) 
where rn > (#{cri.pageNum}-1)*#{cri.amount}
order by re_num desc
